import { useCallback, useEffect, useState } from "react"

const testData = [
    'https://imgs.xkcd.com/comics/easy_or_hard.png',
    'https://imgs.xkcd.com/comics/archimedes_principle.png',
    'https://imgs.xkcd.com/comics/movie_ages.png',
]

const DELAY = 1000; // dev, prd 7000


export const ContentPlayer = () => {
    const playList = testData;
    const [index, setIndex] = useState<number>(0);

    // todo useCallback
    const next = () => {
        setIndex((index + 1) % playList.length);
        setTimeout(next, DELAY);
    }

    // startLoop
    useEffect(() => {
        next();
    }, [playList]);

    return <div className='content-player-container'>
        <img className='content-image' src={playList[index]}></img>
    </div>
}
