import React, { ReactPropTypes } from "react";
import { useCallback, useEffect, useState } from "react"

const testData = [
    'https://imgs.xkcd.com/comics/easy_or_hard.png',
    'https://imgs.xkcd.com/comics/archimedes_principle.png',
    'https://imgs.xkcd.com/comics/movie_ages.png',
]

const INTERVAL = 1000; // dev, prd 7000

interface ContentPlayerState {
    playlist: string[];
    index: number;
}

export class ContentPlayer extends React.Component<ReactPropTypes, ContentPlayerState> {
    constructor(props: ReactPropTypes) {
        super(props);
        this.state = {
            playlist: [...testData],
            index: 0,
        }

    }

    componentDidMount(): void {

    }

    componentWillUnmount(): void {

    }

    render () {
        return <div className='content-player-container'>
            <img className='content-image' src={this.state.playlist[this.state.index]}></img>
        </div>
    }
}


const _ContentPlayer = () => {
    const [playlist, setPlaylist] = useState<string[]>(testData);
    const [index, setIndex] = useState<number>(0);

    // todo useCallback
    const next = useCallback(() => {
        console.log(index, performance.now());
        setIndex((index + 1) % 3);
    }, []);

    // startLoop
    useEffect(() => {
        const id = setInterval(next, INTERVAL);
        return () => clearInterval(id);
    }, []);


}
